menu = {
   [1] = { nome="Mulher e Mídia" , url="https://goo.gl/cev9AP", descricao="Aborda a relação entre as Mulheres e as Mídias, em uma\nroda de debate com comunicadoras, jornalistas e \nblogueiras. Biografia: Jornalista Carmem da Silva\npor Carla Vilaça Sarau: Madu dos Santos, Negra Lud,\nSara Não Tem Nome e Mima Fernandes. o destaque do\nBiografia é a jornalista Carmem da Silva, pela \nblogueira e jornalista Carla Vilaça. O Sarau\napresenta a performer Madu dos Santos, da MC e\nmilitante da cultura Hip-Hop Negra Lud, da \nmultiartista Sara Não Tem Nome e da cantora\nMima Fernandes.", exibicao="17/03/2016",reprise="15/09/2016" },
[2] = { nome="Mulher e Política" , url="https://youtu.be/-Zl_VvJ2fNs", descricao="Para debater a presença das mulheres na politica temos uma conversa com Larissa Amorim Borges, subsecretária de Políticas para Mulheres de Minas Gerais, e Áurea Carolina, cientista política. A biografia da professora e antropóloga mineira Lélia Gonzalez, contamos com Vanessa Eleutério Miranda, professora da UFMG. E tem Sarau com a poeta Nívea Sabino, a artista plástica e performer Camila Lacerda, a cantora Maíra Baldaia e a artista plástica e performer Priscila Resende.", exibicao="24/03/2016",reprise="" },
[3] = { nome="Mulher e Arte" , url="https://youtu.be/wZoKyltkU7A", descricao="No debate sobre a relação e representação da mulher na arte, contamos com a participação das produtoras do DiversaS - Feminismo, Arte e Resistência Paula Kimo, Michelle Sá Maíra Baldaia e Polly Honorato. No Sarau, você confere as apresentações da artista visual Jeannie Helleny, da performer Sthephanny Di Monaco Esteves e da artista plástica e professora Marta Neves. E ainda tem a biografia de Dona Valdete contada por sua filha Marilda Cordeiro.", exibicao="31/03/2016",reprise="22/09/2016" },
[4] = { nome="Mulher e Educação" , url="https://youtu.be/LLRn6RtEqOw", descricao="Conversamos com a educadora Nina Caetano faz uma análise da escola hoje. Ela é inclusiva, horizontal? Como estão as mulheres nesse processo? No Biografia, quem conta a história da educadora Helena Antipoff é a professora Marilene Oliveira Almeida. No Sarau, tem dança com Aline Mathias, música com Bigha Maia, performance com Ártemis Garrido e teatro com Jessica Tamietti.", exibicao="07/04/2016",reprise="29/09/2016" },
[5] = { nome="Paradigmas Sociais" , url="https://youtu.be/k3fB-O0byms", descricao="Passamos por questões estéticas, raciais e sociais para abordar os paradigmas sociais das mulheres. A biografia é da escritora Carolina Maria de Jesus, sua história é contada pela jornalista e fotógrafa Simone Moura. E o nosso Sarau conta com as irmãs gêmeas e palhaças Lais e Thais Oliveira, a cantora Talita Barreto, a performer Noemi Assumpção e a poetiza Zi Reis.", exibicao="14/04/2016",reprise="06/10/2016" },
[6] = { nome="Sexo Ágil" , url="https://youtu.be/qU1B8b6PJCs", descricao="A cantora brasileira Karina Buhr compartilha com a gente suas opiniões sobre mulheres, relações maternas, educação e aborto. A biografia da semana revela a força e a coragem de uma das grandes criadoras da arte da performance, Marina Abramovic.", exibicao="21/04/2016",reprise="13/10/2016" },
[7] = { nome="Sujeitas Históricas" , url="https://youtu.be/IcTyTfBs8eA", descricao="Trazemos a história de grandes mulheres que deixaram um legado importante para as artes, como a escrita, a pintura e a performance. A professora Constância Lima Duarte conta a história da artista Nísia Floresta (escritora) no Biografia. No Sarau, tem muita arte: violão com Jéssica Senhorinii; rap com Emily Roots e poesia com Tatiana Nascimento.", exibicao="28/04/2016",reprise="20/10/2016" },
[8] = { nome="Mulheres em Luta" , url="https://youtu.be/nvUuVbbv-1E", descricao="Acompanhamos diversas mulheres que participaram da Marcha Das Mulheres Pela Democracia, que ocorreu em BH no dia 29/04. A biografia traz a filósofa, escritora e feminista francesa Simone de Beauvoir, autora da famosa frase “Não se nasce mulher, torna-se mulher”. No nosso Sarau, tem poesia com Steefani Stradioto, a performance Fragmentos, da artista Gilmara Oliveira e dança com Ana Tuiã.", exibicao="05/05/2016",reprise="" },
[9] = { nome="Feminismos: Salto às práticas democráticas" , url="https://youtu.be/OV_lK8LX160", descricao="Convidamos a filósofa e escritora Márcia Tiburi, que acredita no feminismo para uma mudança radical na democracia. Alzira Rufino, escritora e ativista política que atua no Movimento de Mulheres Negras, tem sua biografia contada pela historiadora Luana Tolentino. No Sarau com as Pretinhas Correria mandando um rap; música com Paula e Laura Sette, e as meninas do Bloco Bruta Flor.", exibicao="12/05/2016",reprise="" },
[10] = { nome="Lutar", "Ocupar e Re-existir: Ocupação Tina Martins" ,  url="https://youtu.be/6ueUgUMVgUM", descricao="Apresentamos a luta da Ocupação Tina Martins, Mulheres feministas, que fazem parte do Movimento de Mulheres Olga Benário - MG. A militante Thatiane Mátia conta a história de Espertirina, á Tina Martins. Os saraus ficam por conta de Ana Roberto, que canta a canção “Maria da Vila Matilde”, de Elza Soares, e das poetas Gabriela Nascimento e Natana Coelho.",exibicao="19/05/2016",reprise="" },
[11] = { nome="Cone(x)cidades: Conceição do Mato Dentro" , url="https://youtu.be/WCGC-jkTMco", descricao="Mostramos um recorte da vida das mulheres que vivem em Conceição do Mato Dentro, na região central de Minas. No Biografia, a funcionária pública Marina Oliveira conta a história da mãe, Antônia Augusta. No Sarau tem música com o Coral da Terceira Idade, do projeto do Centro de Referência do Idoso, com a jovem Adrielle Moreira, a artista Denise Demiranda e a Dona Ana recitando a poesia “Na praça”!", exibicao="26/05/2016",reprise="" },
[12] = { nome="cone(x)cidades: Indígenas Pataxós" , url="https://youtu.be/0Z0AqrvHIhE", descricao="Fomos a Aldeia Indígena Sede da etnia Pataxó, em Carmésia, a 200 km de BH, trazendo mulheres indígenas Pataxós. No Biografia, Jandaia Pataxó conta a história da mãe, Jitaí Pataxó, que vive na Aldeia Mãe. Lárica Pataxó mostra uma pintura, que representa a união espiritual com Deus para os Pataxós no Sarau, tem a brincadeira Equilíbrio do Bambu, lançamento de arco e flecha e música.", exibicao="02/06/2016",reprise="27/10/2016" },
[13] = { nome="Cultura do Estupro" , url="https://youtu.be/Fl8DgnKaJAY", descricao="Discussão do tema: a cultura do estupro. No dia 1º de junho, mulheres de várias partes do país saíram às ruas para pedir o fim dessa cultura, que é uma das faces mais terríveis do machismo. No debate mulheres que participaram do ato “Todas por Elas”, Biografia da assistente social Gilse consenza. As musicas do Sarau de Verilucy Pinheiro e poesia Laura Colpa.", exibicao="09/06/2016",reprise="" },
[14] = { nome="Mulheres de Mariana" , url="https://youtu.be/t_3zr-BbSps", descricao="O mulhere-se traz as mulheres atingidas pelo rompimento da barragem em Mariana. A biografia de Marta Caetano, no Sarau poesias de Maria do Carmo e Alice Silva.", exibicao="16/06/2016",reprise="03/11/2016" },
[15] = { nome="Sagrado Feminino" , url="https://youtu.be/Q8sxnRWQ4t8", descricao="As mulheres ligadas ao Sagrado Feminino explicam o que significa o sagrado e suas ideias . No Sarau tem performance com a artista Mary Astrus e poesia com Raquel Anwar e a escritora Helena Ferreira. E ainda tem a jovem Maria Laura contando a história de sua avó e a relação delas com o sagrado feminino, no quadro Biografia.", exibicao="23/06/2016",reprise="10/11/2016" },
[16] = { nome="Cone(x)cidades Mulheres Circenses" , url="https://youtu.be/EyxLTZrApr4", descricao="Para mostrar um pouco a realidade de mulheres circenses. Fomos até dois circos: o Mirage Circus e o Atiare's Cirkus, montados em Belo Horizonte. No Biografia, a coreógrafa Cristina Sbano, de uma tradicional família de artistas circenses. No quadro sarau tem muita apresentação circense. A malabarista e acrobata Amanda Jardim e da bailarina Jeniffer Rakmer.", exibicao="30/06/2016",reprise="17/11/2016" },
[17] = { nome="Marcha das Vadias" , url="https://youtu.be/LLtDe86OEIU", descricao="Diversas mulheres saíram às ruas para participar da Marcha das Vadias, ato que acontece todos os anos em várias partes do mundo. No Sarau, tem as meninas dos Bloco Bruta Flor e Tambores de Luta. A Biografia é contada por Letícia, uma das organizadoras do evento, que conta a história de Cristal Lopez, mulher trans e militante de BH.", exibicao="07/07/2016",reprise="" },
[18] = { nome="Literatura – Produção Colaborativa" , url="https://youtu.be/lBdhobKhUMo", descricao="Abordamos a experiência de uma produção literária feminina. No Sarau tem as prosas poéticas que compõem o livro, narradas por Rafaela Fernandes e Patrícia Corrêa - Pat Manoese. O quadro Biografia apresenta a história de Safo, poetisa da Grécia Antiga e figura feminina de grande importância.", exibicao="14/07/2016",reprise="24/11/2016" },
[19] = { nome="Uma por Minuto" , url="https://youtu.be/HIatBG3SNhs", descricao="Diversas mulheres saíram às ruas de BH para participar do manifesto \"UMA POR MINUTO - Pelo Fim da Cultura do Estupro\". No Sarau, temos fortes poesias de Karine Bassi. A Biografia é contada por Isis Medeiros, que fala sobre Alexandra Kollontai, líder revolucionária russa e feminista, além de uma importante teórica do marxismo.", exibicao="21/07/2016",reprise="" },
[20] = { nome="Diversidades" , url="https://youtu.be/BNNWkAkd7Og", descricao="Conversamos com mulheres lésbicas e transexuais,sobre suas perspectivas, identidades, avanços e retrocessos políticos.No Sarau temos performance com as artistas Ana Luisa Santos e Fernanda Branco Polse, música com Aline Alkmim e Janaina Assis. No Biografia temos a pesquisadora Liliane Anderson contando sua trajetória e descoberta como militante e o desafio de ampliar sua luta dentro da sociedade.", exibicao="28/07/2016",reprise="01/12/2016" },
[21] = { nome="Cone(x)cidades - Latinidades" , url="https://youtu.be/oREVn6W0f9g", descricao="Mulheres de diferentes nacionalidades falam sobre representação, reforma política, cultura, ancestralidade e luta! No Sarau tem música com a cubana Teresa Morales e a brasileira Bia Nogueira. No quadro Biografia tem a colombiana e quilombola Carmenza Bambazú Luna Espectral, que iniciou sua atuação feminista através do Hip Hop e criou o método de dança como terapia Bambazú. Ao contar sua trajetória, ela diz sobre a situação política, social e histórica da humanidade em relação à população negra.", "04/08/2016", exibicao="4/08/2016",reprise="08/12/2016" },
[22] = { nome="Representação Feminina nos Quadrinhos" , url="https://youtu.be/NCaYmHp_5HA", descricao="Um universo que durante muito tempo parece ter destacado apenas os homens, e brancos, e heterossexuais, vem recebendo muitas mulheres: o universo dos quadrinhos. No conversações estão artistas do Zinas, coletivo formado por oito mulheres.. No sarau tem as produções do Zinas e no quadro Biografia, a quadrinista Ana Carolinha Cunha e a pesquisadora Dani Marino, falam sobre a super-heroína dos quadrinhos, a Mulher Maravilha.", exibicao="11/08/2016",reprise="15/12/2016" },
[23] = { nome="Mulheres Quilombolas" , url="https://youtu.be/mpSq55SuKhE", descricao="Remontagem do documentário Dandaras: a força da mulher quilombola, produzido pelas antropólogas Ana Carolina Fernandes e Amaralina Fernandes, que percorreram vários quilombos em Minas Gerais para mostrar a força e sabedoria das mulheres quilombolas. No sarau tem apresentação de dança do Quilombo Arturos e música com Maria Aparecida. No Biografia, tem a história da Dona Tiana, do quilombo Carra patos da Tabatinga.", exibicao="18/08/2016", reprise="" },
[24] = { nome="Espaços Públicos" , url="https://youtu.be/bDn2BzDqtF0", descricao="Edição especial: a Crew Minas de Minas, formada pelas graffiteiras de Belo Horizonte Krol (Carolina Jaued), Musa (Louise Líbero), Nica (Nayara Gessyca) e Viber (Lidia Soares). No bate papo elas falam sobre graffiti, espaços públicos, poderes, família e muito mais! No quadro ‪#‎Biografia ‬temos a história do coletivo NINFEIAS, que tem por objetivo o estudo de teorias e práticas feministas performativas.",exibicao="25/08/2016", reprise="22/12/2016" },
[25] = { nome="Comprometimento e Resistência", url ="", descricao="No quadro Conversações Eliane Dias, advogada, coordenadora do SOS Racismo da ALESP e produtora dos Racionais MC's coloca seu olhar em torno de várias questões. No quadro Sarau temos muito ritmo e poesia com Kainná Tawá cantando “Manchete”, Ohana Santana com “Que Assim Seja” e Tamara Franklin com “Vem e Vê”! No quadro Biografia temos a história da Nina Simone, contada por Silvânia Morais, professora e militante da Frente de Mulheres das Brigadas Populares.", exibicao="01/08/2016",reprise="29/12/2016" },
[26] = { nome="Matriarcado - As Filhas da Mãe" , url="https://youtu.be/mpSq55SuKhE", descricao="No quadro Conversações temos Ágatha Flora, Rita Silva, Cida De Amarale Solícita Rodrigues falando sobre o poder transformador do trabalho, da alegria, da palavra! O grupo Filhas da Mãe compõe o quadro Sarau,compreendendo a alegria e a diversão como sabedorias, aspectos fundamentais e transformadores da vida pelo contato com o imaginário através da arte e do samba. No quadro Biografia temos a sabedoria e força de Dona Solícita Rodrigues, contada por sua filha Rita Silva.", exibicao="08/08/2016", reprise="05/01/2016"}
}
